// Helper functions
export function getTechStackById(id: string): TechStackDeepDive | undefined {
  return TECH_STACK_DEEP_DIVE.find((tech) => tech.id === id);
}

export function getTechStacksByCategory(category: TechCategory): TechStackDeepDive[] {
  return TECH_STACK_DEEP_DIVE.filter((tech) => tech.category === category);
}

export function getAllTechNames(): string[] {
  return TECH_STACK_DEEP_DIVE.map((tech) => tech.name);
}

export function searchTechStacks(query: string): TechStackDeepDive[] {
  const lowerQuery = query.toLowerCase();
  return TECH_STACK_DEEP_DIVE.filter(
    (tech) =>
      tech.name.toLowerCase().includes(lowerQuery) ||
      tech.description.toLowerCase().includes(lowerQuery) ||
      tech.subtopics.some((sub) => sub.name.toLowerCase().includes(lowerQuery))
  );
}

export function getCompanyFocusedSubtopics(techId: string, company: string): TechSubtopic[] {
  const tech = getTechStackById(techId);
  if (!tech) return [];
  return tech.subtopics.filter(
    (sub) => sub.companyFocus?.includes(company) || sub.companyFocus?.includes("All Companies")
  );
}
